import React from "react";

function MovieDetails(props) {
  const { movie } = props;

  return (
    <div>
      <img src={movie.poster} alt={movie.title} />
      <h2>{movie.title}</h2>
      <p>Режисер: {movie.director}</p>
      <p>Рік випуску: {movie.year}</p>
      <p>Кіностудія: {movie.studio}</p>
      {/* Додайте інші необхідні вам відомості про фільм */}
    </div>
  );
}

export default MovieDetails;
