import React from "react";
import MovieDetails from "./components/MovieDetails.jsx";
import "./style/movie.css";
function App() {
  const favoriteMovie = [
    {
      title: "Inception",
      director: "Christopher Nolan",
      year: 2010,
      studio: "Warner Bros.",
      poster:
        "https://cdn.27.ua/sc--media--prod/default/ca/20/43/ca2043a4-8c0e-4855-b57a-15e796f5cfcd.jpg",
    },
    {
      title: "The Dark Knight",
      director: "Christopher Nolan",
      year: 2008,
      studio: "Warner Bros.",
      poster:
        "https://cdn.27.ua/sc--media--prod/default/ca/20/43/ca2043a4-8c0e-4855-b57a-15e796f5cfcd.jpg", // Замініть посилання на інший постер
    },
  ];

  return (
    <div>
      {favoriteMovies.map((movie, index) => (
        <div className="movie" key={index}>
          <MovieDetails movie={movie} />
        </div>
      ))}
    </div>
  );
}

export default App;
